mapper.select:
SELECT t.* FROM task t LEFT JOIN `user` u ON t.publish_user_id = u.stuid  

	
		and t.taskname like #{words}
	
	
		and u.schoolid = #{schoolid,jdbcType=INTEGER}
	


ORDER BY t.addtime DESC

SELECT t.* FROM task t LEFT JOIN `user` u ON t.publish_user_id = u.stuid  

	t.state=0
	
		and t.taskname like #{words}
	
	
		and u.schoolid = #{schoolid,jdbcType=INTEGER}
	


ORDER BY t.addtime DESC
